css:
/*style mobile*/
@media screen and (min-width: 992px) and (max-width: 1367px) {
    #nameIf {
        color: #000000;
        margin-left: var(--spacer-1);
        padding: var(--spacer-2);
        position: relative;
        top: var(--spacer-3);
        text-transform: uppercase;
        font-size: 13px;
    }
}

/* style desktop */
main form .form-control {
    color: #3d414a;
    font-size: 15px;
}

.btn.glide__arrow {
    background-color: #ccc;
    border-radius: 100%;
    font-size: 10px;
    padding: 16px;
    /* border-color: black; */
    margin: 0px;
}  
main .footer {
    background-color: #ffffff;
    border-color: var(--primary);
    color: gray;
}
main .footer__title {
    color: grey;
    font-size: 15px;
    font-weight: 300;
}
main .footer__links a {
    color: #000;
    font-size: 12px;
}

main .ecom-credits {
    background-color: #ffffff;
    padding-right: 6px;
    display: none;
}
main .footer__contacts a {
    color: #000;
    font-size: 18px;
}
main .footer p {
    font-size: 14px;
    color: grey;
}
.product__short-stock {
  display: none !important;
}
main .product__buy .quantity-selector__btn-container{
  display: none !important;
}
main .product__buy .btn-lg {
    /* width: 100% !important; */
    box-sizing: border-box;
    font-weight: 600;
    height: 44px;
    padding: 8px 24px;
    max-width: 535px;
    background-color: forestgreen;
    border: none;
    border-radius: 0;
    min-width: 250px !important;
}
